import {
  IS_SEARCHING,
  SEARCH_RESULTS_RECEIVED
} from '../constants/reducer-actions.const';

const initialState = {
  songs: []
};

export default function(state = initialState, action) {
  switch (action.type) {
    case SEARCH_RESULTS_RECEIVED:
      return {
        ...state,
        playlistSongs: action.payload
      };
    case IS_SEARCHING:
      return {
        ...state,
        playlistSongs: action.payload
      };
    default:
      return state;
  }
}
